glsl3.vert:
  precision highp float;
  layout(std140) uniform CCGlobal {
    highp   vec4 cc_time;
    mediump vec4 cc_screenSize;
    mediump vec4 cc_nativeSize;
    mediump vec4 cc_probeInfo;
    mediump vec4 cc_debug_view_mode;
  };
  layout(std140) uniform CCCamera {
    highp   mat4 cc_matView;
    highp   mat4 cc_matViewInv;
    highp   mat4 cc_matProj;
    highp   mat4 cc_matProjInv;
    highp   mat4 cc_matViewProj;
    highp   mat4 cc_matViewProjInv;
    highp   vec4 cc_cameraPos;
    mediump vec4 cc_surfaceTransform;
    mediump vec4 cc_screenScale;
    mediump vec4 cc_exposure;
    mediump vec4 cc_mainLitDir;
    mediump vec4 cc_mainLitColor;
    mediump vec4 cc_ambientSky;
    mediump vec4 cc_ambientGround;
    mediump vec4 cc_fogColor;
    mediump vec4 cc_fogBase;
    mediump vec4 cc_fogAdd;
    mediump vec4 cc_nearFar;
    mediump vec4 cc_viewPort;
  };
  #if USE_LOCAL
    layout(std140) uniform CCLocal {
      highp mat4 cc_matWorld;
      highp mat4 cc_matWorldIT;
      highp vec4 cc_lightingMapUVParam;
      highp vec4 cc_localShadowBias;
      highp vec4 cc_reflectionProbeData1;
      highp vec4 cc_reflectionProbeData2;
      highp vec4 cc_reflectionProbeBlendData1;
      highp vec4 cc_reflectionProbeBlendData2;
    };
  #endif
  in vec3 a_position;
  in vec2 a_texCoord;
  out vec2 uv;
  #if USE_LOCAL
    in vec4 a_color;
    out vec4 v_color;
  #endif
  vec4 vert() {
    vec4 pos = vec4(a_position, 1);
    #if USE_LOCAL
      pos = cc_matWorld * pos;
      v_color = a_color;
    #endif
    pos = cc_matViewProj * pos;
    uv = a_texCoord;
    return  pos;
  }
void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
uniform sampler2D cc_spriteTexture;
in vec2 uv;
layout(std140) uniform UBO {
  vec4 glowColor;
  vec2 textureAspect;
  float glowSpread;
};
vec4 frag () {
  vec4 texColor = texture(cc_spriteTexture, uv);
  float centerAlpha = texColor.a;
  if (centerAlpha > 0.5) return texColor;
  const int DIR_COUNT = 80;
  const int STEP_COUNT = 20;
  float stepSize = glowSpread / float(STEP_COUNT);
  float minDist = glowSpread;
  for (int dir = 0; dir < DIR_COUNT; dir++) {
    float angle  = 6.2831853 * float(dir) / float(DIR_COUNT);
    vec2  dirVec = vec2(cos(angle), sin(angle));
    for (int s = 1; s <= STEP_COUNT; s++) {
      float dist = float(s) * stepSize;
      vec2  offset = dirVec * dist / textureAspect;
      float a = texture(cc_spriteTexture, uv + offset).a;
      if (a > 0.5) {
        minDist = min(minDist, dist);
        break;
      }
    }
  }
  vec4 color = texColor;
  #if USE_GLOW_DECAY
    float glowFactor = smoothstep(glowSpread, 0.0, minDist);
    color = glowColor * glowFactor;
  #else
    if (minDist < glowSpread) {
      color = glowColor;
    }
  #endif
  return color;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

glsl1.vert:
  precision highp float;
  uniform highp mat4 cc_matViewProj;
  #if USE_LOCAL
    uniform highp mat4 cc_matWorld;
  #endif
  attribute vec3 a_position;
  attribute vec2 a_texCoord;
  varying vec2 uv;
  #if USE_LOCAL
    attribute vec4 a_color;
    varying vec4 v_color;
  #endif
  vec4 vert() {
    vec4 pos = vec4(a_position, 1);
    #if USE_LOCAL
      pos = cc_matWorld * pos;
      v_color = a_color;
    #endif
    pos = cc_matViewProj * pos;
    uv = a_texCoord;
    return  pos;
  }
void main() { gl_Position = vert(); }

glsl1.frag:
precision highp float;
uniform sampler2D cc_spriteTexture;
varying vec2 uv;
   uniform vec4 glowColor;
   uniform vec2 textureAspect;
   uniform float glowSpread;
vec4 frag () {
  vec4 texColor = texture2D(cc_spriteTexture, uv);
  float centerAlpha = texColor.a;
  if (centerAlpha > 0.5) return texColor;
  const int DIR_COUNT = 80;
  const int STEP_COUNT = 20;
  float stepSize = glowSpread / float(STEP_COUNT);
  float minDist = glowSpread;
  for (int dir = 0; dir < DIR_COUNT; dir++) {
    float angle  = 6.2831853 * float(dir) / float(DIR_COUNT);
    vec2  dirVec = vec2(cos(angle), sin(angle));
    for (int s = 1; s <= STEP_COUNT; s++) {
      float dist = float(s) * stepSize;
      vec2  offset = dirVec * dist / textureAspect;
      float a = texture2D(cc_spriteTexture, uv + offset).a;
      if (a > 0.5) {
        minDist = min(minDist, dist);
        break;
      }
    }
  }
  vec4 color = texColor;
  #if USE_GLOW_DECAY
    float glowFactor = smoothstep(glowSpread, 0.0, minDist);
    color = glowColor * glowFactor;
  #else
    if (minDist < glowSpread) {
      color = glowColor;
    }
  #endif
  return color;
}
void main() { gl_FragColor = frag(); }
