glsl3.vert:
precision highp float;
in vec3 a_position;
in vec2 a_texCoord;
out vec2 v_uv;
void main () {
  vec4 pos = vec4(a_position, 1.0);
  gl_Position = pos;
  v_uv = a_texCoord;
}

glsl3.frag:
precision highp float;
in vec2 v_uv;
uniform sampler2D inputTexture;
vec4 frag () {
  vec4 color = texture(inputTexture, v_uv);
  vec4 green = vec4(0.0, 1.0, 0.0, 1.0);
  return mix(color, green, 0.3);
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

glsl1.vert:
precision highp float;
attribute vec3 a_position;
attribute vec2 a_texCoord;
varying vec2 v_uv;
void main () {
  vec4 pos = vec4(a_position, 1.0);
  gl_Position = pos;
  v_uv = a_texCoord;
}

glsl1.frag:
precision highp float;
varying vec2 v_uv;
uniform sampler2D inputTexture;
vec4 frag () {
  vec4 color = texture2D(inputTexture, v_uv);
  vec4 green = vec4(0.0, 1.0, 0.0, 1.0);
  return mix(color, green, 0.3);
}
void main() { gl_FragColor = frag(); }
