glsl3.vert:
precision highp float;
in vec3 a_position;
in vec2 a_texCoord;
out vec2 uv;
void main () {
  vec4 pos = vec4(a_position, 1.0);
  gl_Position = pos;
  uv = a_texCoord;
}

glsl3.frag:
precision highp float;
in vec2 uv;
layout(std140) uniform UBO {
  float strength;
  float speed;
  float time;
};
uniform sampler2D inputTexture;
uniform sampler2D noiseTexture;
vec4 frag () {
  vec2 noiseUV = fract(uv + vec2(0.0, time * speed));
  vec2 noise = texture(noiseTexture, noiseUV).rg;
  noise = noise * 2.0 - 1.0;
  vec2 distortedUV = uv + noise * strength;
  return texture(inputTexture, distortedUV);
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

glsl1.vert:
precision highp float;
attribute vec3 a_position;
attribute vec2 a_texCoord;
varying vec2 uv;
void main () {
  vec4 pos = vec4(a_position, 1.0);
  gl_Position = pos;
  uv = a_texCoord;
}

glsl1.frag:
precision highp float;
varying vec2 uv;
   uniform float strength;
   uniform float speed;
   uniform float time;
uniform sampler2D inputTexture;
uniform sampler2D noiseTexture;
vec4 frag () {
  vec2 noiseUV = fract(uv + vec2(0.0, time * speed));
  vec2 noise = texture2D(noiseTexture, noiseUV).rg;
  noise = noise * 2.0 - 1.0;
  vec2 distortedUV = uv + noise * strength;
  return texture2D(inputTexture, distortedUV);
}
void main() { gl_FragColor = frag(); }
