float luminance(vec3 c) {
  return dot(c, vec3(0.2126, 0.7152, 0.0722));
}
vec3 setLum(vec3 c, float l) {
  float d = l - luminance(c);
  return c + vec3(d);
}
vec3 setSat(vec3 c, float s) {
  float l = luminance(c);
  vec3 grey = vec3(l);
  return mix(grey, c, s);
}
float sat(vec3 c) {
  return max(max(c.r, c.g), c.b) - min(min(c.r, c.g), c.b);
}
vec3 blendOverlay(vec3 base, vec3 blend) {
  return mix(2.0 * base * blend, 1.0 - 2.0 * (1.0 - base) * (1.0 - blend), step(0.5, base));
}
vec3 blendDarken(vec3 base, vec3 blend) {
  return min(base, blend);
}
vec3 blendLighten(vec3 base, vec3 blend) {
  return max(base, blend);
}
vec3 blendColorDodge(vec3 base, vec3 blend) {
  return base / max(vec3(EPSILON), 1.0 - blend);
}
vec3 blendColorBurn(vec3 base, vec3 blend) {
  return 1.0 - (1.0 - base) / max(vec3(EPSILON), blend);
}
vec3 blendHardLight(vec3 base, vec3 blend) {
  return blendOverlay(blend, base);
}
vec3 blendSoftLight(vec3 base, vec4 blend) {
    vec3 A = base - (1.0 - 2.0 * blend.rgb) * base * (1.0 - base);
    vec3 usePoly = step(base, vec3(0.25));
    vec3 Gpoly = ((vec3(16.0) * base - vec3(12.0)) * base + vec3(4.0)) * base;
    vec3 G = usePoly * Gpoly + (vec3(1.0) - usePoly) * sqrt(base);
    vec3 B = base + (2.0 * blend.rgb - 1.0) * (G - base);
    vec3 soft = mix(A, B, step(vec3(0.5), blend.rgb));
    return mix(base, soft, clamp(blend.a, 0.0, 1.0));
}
vec3 blendDifference(vec3 base, vec3 blend) {
  return abs(base - blend);
}
vec3 blendExclusion(vec3 base, vec3 blend) {
  return base + blend - 2.0 * base * blend;
}
vec3 blendHue(vec3 base, vec3 blend) {
  float s = sat(base);
  float l = luminance(base);
  return setLum(setSat(blend, s), l);
}
vec3 blendSaturation(vec3 base, vec3 blend) {
  float s = sat(blend);
  float l = luminance(base);
  return setLum(setSat(base, s), l);
}
vec3 blendColor(vec3 base, vec3 blend) {
  return setLum(blend, luminance(base));
}
vec3 blendLuminosity(vec3 base, vec3 blend) {
  return setLum(base, luminance(blend));
}
